graphql.query:
query getOrderById($id: ID!) {
  node(id: $id) {
    id
    ... on Order {
      name
      id
      createdAt
      currencyCode
      totalPriceSet {
        shopMoney {
          amount
          currencyCode
        }
      }
      lineItems(first: 10) {
        edges {
          node {
            title
            quantity
            sku
            variant {
              id
              title
              product {
                id
                title
              }
            }
            originalUnitPriceSet {
              shopMoney {
                amount
                currencyCode
              }
            }
            discountedTotalSet {
              shopMoney {
                amount
                currencyCode
              }
            }
            taxLines {
              title
              rate
              priceSet {
                shopMoney {
                  amount
                  currencyCode
                }
              }
            }
          }
        }
      }
      shippingLines(first: 5) {
        edges {
          node {
            title
            originalPriceSet {
              shopMoney {
                amount
                currencyCode
              }
            }
            discountedPriceSet {
              shopMoney {
                amount
                currencyCode
              }
            }
          }
        }
      }
      fulfillments {
        id
        status
        location {
          id
          name
        }
      }
      transactions {
        id
        gateway
        amountSet {
          shopMoney {
            amount
            currencyCode
          }
        }
        kind
        status
        processedAt
      }
    }
  }
}

