mutation OrderCreate($order: OrderCreateOrderInput!, $options: OrderCreateOptionsInput) {
  orderCreate(order: $order, options: $options) {
    userErrors {
      field
      message
    }
    order {
      name
      id
      displayFinancialStatus
      displayFulfillmentStatus
      createdAt
      processedAt
    }
  }
}

